    <hr>
    <div class=\"row\">
        <div class=\"col-md-6\">
            
            {% set formHtml %}
                {{ form_start(form, {action:path('order_recap')}) }}
                {{ form_end(form) }}
            {% endset %}

            {{ formHtml|replace({'[br]' : '<br/>'})|raw }}
            
        </div>
        <div class=\"col-md-6\">
        <div class=\"text-center\">
            <b>Récap de ma commande</b><br/>
            <p> Voici le récapitulatif de votre panier </p>
        </div>
            
            <div class=\"order-summary\">
            {% for key,product in cart %}
            <div class=\"row {% if key > 0 %}mt-2{% endif %}\">
                <div class=\"col -2\">
                    <img src=\"/uploads/{{product.product.illustration }}\" alt=\" {{product.product.name}}\" height=\"80px\"><br/>
                </div>
                <div class=\"col-8 my-auto\" >
                    {{ product.product.name}}<br/>
                    <small>{{ product.product.description }}</small>
                </div>

                <div class=\"col-2 my-auto\">
                    x {{ product.quantity }}
                </div>
        </div>
        {% endfor %}
            </div>
            <b>Bouton pour payer</b>
        </div>
    </div>
{% endblock %}
